mapper.select:

	select id, password, nickname, userimage, usercontent from users where 
	id=#{id} and 
	password = #{password}

mapper.update:

	update users set
	nickname = #{nickName},
	userimage = #{userImage},
	usercontent = #{userContent}
	where id = #{id}


